'use strict';

// MAIN //

/**
* Middleware function to handle request cookies.
*
* @private
* @param {Object} request - request object
* @param {Object} reply - reply object
* @param {Callback} next - callback to invoke upon completion
* @returns {void}
*/
function cookies( request, reply, next ) {
	var app;
	var o;
	var c;

	app = this.appinfo; // eslint-disable-line no-invalid-this

	o = request.locals;
	c = request.cookies;

	// Documentation theme:
	o.theme = c.theme || app.theme;

	// Documentation mode:
	o.mode = c.mode || app.mode;

	// Code example syntax:
	o.exampleSyntax = c.examplesyntax || app.exampleSyntax;

	// Previous/next package navigation:
	o.prevNextNavigation = c.prevnextnavigation || app.prevNextNavigation;

	next();
}


// EXPORTS //

module.exports = cookies;
