'use strict';

// MODULES //

var basename = require( 'path' ).basename;
var pkgKind = require( 'package-kind' );


// MAIN //

/**
* Generates a document title from a specified package URL.
*
* @private
* @param {string} pkg - package URL
* @returns {string} document title
*
* @example
* var t = pkg2title( 'math/base/special/sin' );
* // returns 'sin | math.base | stdlib'
*
* @example
* var t = pkg2title( 'utils/copy' );
* // returns 'copy | utils | stdlib'
*/
function pkg2title( pkg ) {
	var kind;
	var str;

	// First resolve the package basename (e.g., 'math/base/special/sin' => 'sin')
	str = basename( pkg ) + ' | ';

	// Next try resolving the package "kind" (e.g., 'math.base'):
	kind = pkgKind( pkg );
	if ( kind ) {
		str += kind + ' | ';
	}
	// Append the project name:
	str += 'stdlib';

	return str;
}


// EXPORTS //

module.exports = pkg2title;
