'use strict';

// MAIN //

/**
* Returns the last index of a specified character.
*
* @private
* @param {string} str - string to search
* @param {string} ch - character to match
* @returns {integer} index
*/
function lastIndexOf( str, ch ) {
	var len;
	var i;

	len = str.length;
	for ( i = len-1; i >= 0; i-- ) {
		if ( str[ i ] === ch ) {
			return i;
		}
	}
	return -1;
}


// EXPORTS //

module.exports = lastIndexOf;
